member.example:
bool isVirtualDevice = pnpDevice.IsVirtual(pDevice =>
{
    List<string>? hardwareIds = pDevice.GetProperty<string[]>(DevicePropertyKey.Device_HardwareIds).ToList();

    // hardware IDs of root hubs/controllers that emit supported virtual devices as sources
    string[] excludedIds =
    {
		@"ROOT\HIDGAMEMAP", // reWASD
		@"ROOT\VHUSB3HC", // VirtualHere
		@"Nefarius\ViGEmBus\Gen1", // ViGemBus v1 
		@"Nefarius\ViGEmBus\Gen2", // ViGemBus v2 
		@"Nefarius\VirtualPad" // VirtualPad
	};

    return hardwareIds.Any(id => excludedIds.Contains(id.ToUpperInvariant()));
});
